package com.spring.springoopcw.Vendor;

import jakarta.persistence.Entity;
import jakarta.persistence.GeneratedValue;
import jakarta.persistence.GenerationType;
import jakarta.persistence.Id;

/**
 * VendorEntity is a JPA entity representing a vendor in the ticketing system.
 * It includes the vendor's unique ID, their vendor ID, and the number of tickets they have added.
 */
@Entity
public class VendorEntity {
    // The unique ID of the vendor, auto-generated by the database
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;
    private int vendorId;
    private int ticketsAdded;

    // Getters and Setters
    public Long getId() { return id; }
    public void setId(Long id) { this.id = id; }

    public int getVendorId() { return vendorId; }
    public void setVendorId(int vendorId) { this.vendorId = vendorId; }

    public int getTicketsAdded() { return ticketsAdded; }
    public void setTicketsAdded(int ticketsAdded) { this.ticketsAdded = ticketsAdded; }
}

